function letterbox(i){var f=document.getElementById("letterbox");m=document.getElementById("letterbox-message");f.addEventListener("submit",function(e){e.preventDefault&&e.preventDefault();var r=new XMLHttpRequest,data=new FormData(f);r.open("POST","http://localhost:3000/"+i);if(m){r.onload=function(e){m.style.display="block";if(this.status==200){m.innerHTML="Form sent successfully."}else{m.className="error";m.innerHTML="There was an error."}}}r.send(data)})}
